CREATE TABLE tipo_consulta (id int(10) NOT NULL comment 'id de tipo consulta', descripcion varchar(100) NOT NULL UNIQUE comment 'descripcion o nombre de la consulta', PRIMARY KEY (id)) comment='Tipos de consulta a realizar';
CREATE TABLE usuario (id int(10) NOT NULL, nombre_usuario varchar(100) NOT NULL comment 'nombre de usuario', contrasena varchar(255) NOT NULL comment 'contrasena del usuario', PRIMARY KEY (id), UNIQUE INDEX (nombre_usuario)) comment='tabla para almacenar usuarios';
CREATE TABLE consultas (id int(10) NOT NULL comment 'id de la consulta', cantidad_consultada float NOT NULL comment 'cantidad a cambiar', cantidad_cambiada float NOT NULL comment 'cantidad resultante en el cambio', fecha_cambio timestamp NOT NULL comment 'fecha en la que se realizo la consulta', tipo_consulta_id int(10) NOT NULL comment 'id del tipo de consulta', usuario_id int(10) NOT NULL comment 'id del usuario que realizo la consulta', PRIMARY KEY (id));
ALTER TABLE consultas ADD INDEX FKconsultas268112 (tipo_consulta_id), ADD CONSTRAINT FKconsultas268112 FOREIGN KEY (tipo_consulta_id) REFERENCES tipo_consulta (id);
ALTER TABLE consultas ADD INDEX FKconsultas15191 (usuario_id), ADD CONSTRAINT FKconsultas15191 FOREIGN KEY (usuario_id) REFERENCES usuario (id);
